Item.method_code:
var report_type="";
var typeID="";
if (this.dom.xml!=="")
{
	// this is an Item report
	if (this.getType()==="PackageDefinition") {report_type="PACKAGE";}
	else if (this.getType()==="ItemType") {report_type="ItemType";}
	else
	{
	  top.aras.AlertError("Cannot determine type for report");
	  return;
	}
	typeID=this.getID();
}
else
{
  // this is a generic report, show report for ItemType in Main Grid
  report_type="ItemType";
  typeID=top.main.work.itemTypeID;
}

var options ="status=no,toolbar=no,menubar=no,location=no,resizable=yes,scrollbars=yes";

var body = "<typeID>"+typeID+"</typeID><report_type>"+report_type+"</report_type>";
window.open("../scripts/DashboardEx10SP2.aspx?methodName=DMDiagram2&body="+body,null,options);
return "<html/>";
