Item.method_code:
var dest_url=document.thisItem.getProperty("destination_url");
var xmlhttp = new XMLHttpRequest();
xmlhttp.open("GET", dest_url + "Server/DBList.aspx", false);
xmlhttp.send();

var resXMLDOM = new XmlDocument();
resXMLDOM.loadXML(xmlhttp.responseText);

var dbs = resXMLDOM.selectNodes("/DBList/DB"),
	databaseSelect = document.getElementById("F63193132F9D49AA8D38AE3DFA34DC55"), db, newOption, i;

for (i = 0; i < dbs.length; i++) {
	db = dbs[i];
	newOption = document.createElement("option");
	newOption.text = db.getAttribute("id");
	newOption.setAttribute("value", newOption.text);
	databaseSelect.add(newOption);
}
