Item.method_code:
var $cu =document.thisItem.getProperty("tools_folder")+"\\consoleUpgrade\\consoleUpgrade.exe";
var $svr=document.thisItem.getProperty("destination_url")+ "server/InnovatorServer.aspx";
var $db =document.thisItem.getProperty("destination_db");
var $usr=document.thisItem.getProperty("destination_user");
var $pw_cu =document.thisItem.getProperty("destination_pw");
var $dir=document.thisItem.getProperty("export_folder");
var $mf=$dir + "\\imports.mf";
var $rel ="11.0";
var $log=document.thisItem.getProperty("tools_folder")+"\\consoleUpgrade\\log\\cm_import.log";
var $descr ="ConfigManager";


var $cu_cmd = "\""+ $cu + "\" ";
$cu_cmd += "server=\""+$svr + "\" ";
$cu_cmd += "database=\"" +$db  + "\" ";
$cu_cmd += "login=\""+$usr  + "\" ";
$cu_cmd += "password=\""+$pw_cu + "\" import merge verbose ";
$cu_cmd += "dir=\""+$dir  + "\" ";
$cu_cmd += "mfFile=\""+$mf  + "\" ";
$cu_cmd += "release=\""+ $rel  + "\" ";
$cu_cmd += "log=\""+$log + "\" ";
$cu_cmd += "description=\""+$descr  + "\" ";

try   {
    var objShell = new ActiveXObject("wscript.shell");
    objShell.Run("%comspec% /k \""+ $cu_cmd + "\"", 1 , true);
  } catch(e) {
    top.aras.AlertError(e.message);
  }
  
top.aras.AlertSuccess("Import complete, see log file in ConsoleUpgrade folder")
