Item.method_code:
var this_id=this.getProperty("element_id");
var this_group=this.getProperty("package_group");
if (this_group === "Method"||this_group === "Workflow Map")
{
    var qry1=this.newItem(this_group);
    qry1.setProperty("config_id",this.getProperty("element_id"));
    qry1.setProperty("is_latest","1");
    var res=qry1.apply("get");
    if (res.isError()) {top.aras.AlertError(res.getErrorString());return;}
    this_id=res.getID();
}
top.aras.uiShowItem(this_group,this_id);
